mapper.select:
    select
   o.orderId,o.orderDate,o.state,u.userId,u.realName,u.sex,s.smId,s.name as sname,h.hpId,h.name as hname
from orders o,users u,setmeal s,hospital h

where o.userId=u.userId
       and
			 o.smId=s.smId
			 and
			 o.hpId=h.hpId

      and u.userId=#{userId}


      and u.realName=#{realName}


      and u.sex=#{sex}


      and s.smId=#{smId,jdbcType=INTEGER}


      and o.orderDate=#{orderDate,jdbcType=Date}


      and o.state=#{state,jdbcType=INTEGER}

order by o.orderDate
